import React, { Component } from 'react'
import Image from 'react-bootstrap/Image'
import piano from '../assets/img/piano3.jpg'
import camera from '../assets/img/camera2.jpg'
import soundwave from '../assets/img/soundwave2.jpg'

class CollapseImg extends Component {
    constructor(props) {
        super(props);
        this.handleMouseHover = this.handleMouseHover.bind(this);
        this.state = {
            isHovering: false,
            imgName:'',
            imgMsg:''
        };

        this.handleWhichImg(this.props.imgNumber);
    }

    handleWhichImg(imgNumber){
        if(imgNumber === '1'){
            this.setState({
                imgName: piano,
                imgMsg: "A MIDI system which allows you to play a whimsical tune! <br />By using either the virtual keyboard or the musical whitebox, you can play a wide variety of musical sounds. <br />You have the option to change the: chord mode, major or minor key, or even the musical instrument!"
            });
            console.log("***** : ", imgNumber);
            console.log("***** : ", this.imgMsg);
            console.log("***** : ", this.imgName)
        }
    }

    handleMouseHover() {
        this.setState(this.toggleHoverState);
    }

    toggleHoverState(state) {
        return {
            isHovering: !state.isHovering,
        };
    }

    render() {
    return (
        <div>

            <Image src={this.imgName} style={{width:"260px", height:"400px"}}
                onMouseEnter={this.handleMouseHover}
                onMouseLeave={this.handleMouseHover}
            />
            {
                this.state.isHovering &&
                <div>
                    {this.imgMsg}
                </div>
            }
        </div>
    );
    }
}

export default CollapseImg;
