import React from 'react'
import Image from 'react-bootstrap/Image'
import headerImg from '../assets/35mm.jpg'
import downArrow from '../assets/icon/down-arrow.png'

function Header(){
    return(
        <div className='Header-Img'>
            <Image src={headerImg} fluid style={{marginTop: 70}}/> 
            <div className='Header-Text'>
                <p>C h r i s t i n e &nbsp;T s e</p>
                <p style={{color: '#000', fontSize: '16px'}}>HTML5 Game Developer • Chinese-Jamaican • HKUST Graduate • Toronto Based</p>
            </div>
            <div style={{backgroundColor:'red', alignContent:'center', textAlign:'center'}}>
                <Image src={downArrow} style={{height:'60px', width:'60px'}}/>
            </div>
        </div>

    );
}

export default Header;
