import React from 'react'
import Image from 'react-bootstrap/Image'
import midi from '../assets/img/piano3.jpg'
import img from '../assets/img/camera2.jpg'
import audio from '../assets/img/soundwave2.jpg'

class CollapseImg extends React.Component {
    constructor(props) {
    super(props);

    this.state = {
        open: false,
    };
    }

    render() {
        const { open } = this.state;
        return (
        <>
            <Image src={props.name} style={{width:"260px", height:"400px"}}
                onH
                onClick={() => this.setState({ open: !open })}
            />

            <Collapse in={this.state.open}>
                <p align="justify">A MIDI system which allows you to play a whimsical tune! <br />By using either the virtual keyboard or the musical whitebox, you can play a wide variety of musical sounds. <br />You have the option to change the: chord mode, major or minor key, or even the musical instrument! 
                </p>
            </Collapse>
        </>
        );
    }
}

render(<CollapseImg />);
export default CollapseImg;
